import { useRouter } from 'next/router';
import useSWR from 'swr';

function fetcher(url) {
  return fetch(url).then(r => r.json());
}

export default function Index() {
  const { query } = useRouter();
  const { data, error } = useSWR('/api/tarot', fetcher);
  // The following line has optional chaining, added in Next.js v9.1.5,
  // is the same as `data && data.name`
  const name = data?.name;
  let number = data?.number;
  let fortune = data?.fortune_telling
  let image = data?.img
  if (!data) number = 'Loading...';
  if (error) number = 'Failed to fetch a card.';
  let img = '/api/cards' + {image}

  return (
    <main className="center">
      <div className="number">{number}</div>
      {number && <span className="name">{name}</span>}
      <div className="number">{fortune}</div>
      <div className="number"> {image} </div>

      <style jsx>{`
        main {
          width: 90%;
          max-width: 900px;
          margin: 300px auto;
          text-align: center;
        }
        .number {
          font-family: cursive;
          color: #e243de;
          font-size: 24px;
          padding-bottom: 10px;
        }
        .name {
          font-family: sans-serif;
          color: #559834;
          font-size: 20px;
        }
      `}</style>
    </main>
  );
}
